import React, { useState } from 'react';

// Dummy components to make the app runnable.
// In a real application, you would import these from separate files.
const AdvancedChat = () => (
  <div className="component-container">
    <p>A sophisticated chat interface with AI-powered insights.</p>
  </div>
);

const BasicChat = () => (
  <div className="component-container">
    <p>A simple, direct chat for quick queries.</p>
  </div>
);

const ReportAssistant = () => (
  <div className="component-container">
    <p>Generate detailed medical reports with ease.</p>
  </div>
);

// Main application component
const App = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  // The CSS for the entire app is defined as a string within the component.
  // This is a common pattern for single-file web applications.
  const styleSheet = `
    @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');

    :root {
      --bg-color: #f0f4f8;
      --primary-color: #3b82f6;
      --text-color: #1f2937;
      --card-bg: #ffffff;
      --footer-color: #9ca3af;
      --card-hover-shadow: rgba(0, 0, 0, 0.15);
      --card-default-shadow: rgba(0, 0, 0, 0.08);
    }

    body {
      margin: 0;
    }

    .container {
      background-color: var(--bg-color);
      min-height: 100vh;
      padding: 4rem 1rem;
      font-family: 'Inter', sans-serif;
      display: flex;
      flex-direction: column;
      align-items: center;
      color: var(--text-color);
      transition: background-color 0.3s ease;
    }

    .heading {
      text-align: center;
      color: var(--primary-color);
      margin-bottom: 3.5rem;
      font-size: 2.75rem;
      font-weight: 700;
      letter-spacing: -0.5px;
    }

    .card-container {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 2.5rem;
      width: 100%;
      max-width: 1200px;
    }

    .card {
      background: var(--card-bg);
      border-radius: 16px;
      box-shadow: 0 8px 24px var(--card-default-shadow);
      padding: 2.5rem;
      text-align: center;
      transition: transform 0.3s cubic-bezier(0.25, 0.8, 0.25, 1), box-shadow 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);
      border: 1px solid #e2e8f0;
      cursor: pointer;
    }

    .card-hover {
      transform: translateY(-8px);
      box-shadow: 0 16px 32px var(--card-hover-shadow);
    }

    .card-title {
      font-size: 1.5rem;
      font-weight: 600;
      color: var(--primary-color);
      margin-bottom: 1rem;
    }

    .component-container {
      padding-top: 1rem;
      font-size: 0.95rem;
      color: #4b5563;
    }

    .footer {
      text-align: center;
      color: var(--footer-color);
      margin-top: 4rem;
      font-size: 0.875rem;
    }

    @media (max-width: 640px) {
      .container {
        padding: 2rem 1rem;
      }
      .heading {
        font-size: 2.25rem;
      }
    }
  `;

  return (
    <>
      <style>{styleSheet}</style>
      <div className="container">
        <h1 className="heading">MediBot</h1>
        <div className="card-container">
          {[{ title: 'Advanced Chat' }, { title: 'Basic Chat' }, { title: 'Report Assistant' }].map((item, index) => (
            <div
              key={index}
              className={`card ${hoveredCard === index ? 'card-hover' : ''}`}
              onMouseEnter={() => setHoveredCard(index)}
              onMouseLeave={() => setHoveredCard(null)}
            >
              <h2 className="card-title">{item.title}</h2>
              {index === 0 && <AdvancedChat />}
              {index === 1 && <BasicChat />}
              {index === 2 && <ReportAssistant />}
            </div>
          ))}
        </div>
        <footer className="footer">
          &copy; 2025 MediBot. All rights reserved.
        </footer>
      </div>
    </>
  );
};

export default App;
